function addshow() {
  document.getElementById("absolute-right").classList.add("show");
}
function removeshow() {
  document.getElementById("absolute-right").classList.remove("show");
}

function shownegotiationsidebar() {
  document
    .getElementsByClassName("negotiation-sidebar")[0]
    .classList.add("show");
}

function hidenegotiationsidebar() {
  document
    .getElementsByClassName("negotiation-sidebar")[0]
    .classList.remove("show");
}

function changecolumn(classes) {
  document.getElementById("changecolumn").setAttribute("class", classes);
}

let dropcontainer = document.getElementById("navbarsExample05");
let drop = dropcontainer.getElementsByClassName("dropdown-menu");

for (let i = 0; i < drop.length; i++) {
  drop[i].addEventListener("click", function (e) {
    e.stopPropagation();
  });
}

function showfilter() {
  document.getElementById("second-menu").classList.add("opened");
}
function hidefilter() {
  document.getElementById("second-menu").classList.remove("opened");
}

function showfilter_right() {
  document.getElementById("filtersidebar").classList.add("opened");
}
function hidefilter_right() {
  document.getElementById("filtersidebar").classList.remove("opened");
}

function desktopsearch() {
  var input, filter, ul, li, a, i, txtValue;
  input = document.getElementById("mysearchinput");
  document
    .getElementsByClassName("desktop-search-holder")[0]
    .classList.add("searching");
  filter = input.value.toUpperCase();
  ul = document.getElementById("desktop-search-drop");
  li = ul.getElementsByClassName("list-group-item");
  for (i = 0; i < li.length; i++) {
    a = li[i];
    txtValue = a.textContent || a.innerText;
    if (txtValue.toUpperCase().indexOf(filter) > -1) {
      li[i].classList.remove("d-none");
    } else {
      li[i].classList.add("d-none");
    }
  }
}

var input, ul, li, i;
input = document.getElementById("mysearchinput");
ul = document.getElementById("desktop-search-drop");
li = ul.getElementsByClassName("list-group-item");
console.log(li.length);
for (i = 0; i < li.length; i++) {
  li[i].addEventListener("click", function () {
    console.log("dbcd");
    input.value = "";
    document
      .getElementsByClassName("desktop-search-holder")[0]
      .classList.remove("searching");
  });
}

